			 if(data.code == 1){				$("#content").html(data.content);				copy_code();			 }else{				jAlert(data.msg, "错误信息");				$("#content").html("");			 }		   }, "json");        }    }); } </script><SCRIPT type="text/javascript">var times = 0;function load_category(n,catid){	$("#catid").val(catid);	$.ajaxSetup({cache:false});	$('span#load_category_'+n).load('/index.php/process/geturl/subcategory/'+catid+'/'+n);}$(function (){		$("#del").bind("click",function(){		 		var catid = $("#catid").val();		var url = $("#url").val();		var num = $("#num").val();		if(catid == 0){			jAlert("请先选择类别", "错误信息");			return;		}		if(url == ''){			jAlert("请填写阿里巴巴URL地址", "错误信息");			return;		}		$("#content").html("正在加载...");		$.post("<?php echo site_url('process/geturl/makeurl')?>", { catid: catid ,url: url,num:num},	   function(data){		 if(data.code == 1){			$("#content").html(data.content);			copy_code();		 }else{			jAlert(data.msg, "错误信息");			$("#content").html("");		 }	   }, "json");	});})</SCRIPT></head><body <?php if($catid){echo "onload=\"load_category(1,{$catid})\"";}?>><div id="msgbox" onmouseover="closemsg();" style="display:none;"></div><div class="menu" onselectstart="return false" id="destoon_menu"><table border="0" cellpadding="0" cellspacing="0" width="100%"><tbody><tr><td valign="bottom"><table cellpadding="0" cellspacing="0"><tbody><tr><td width="10">&nbsp;</td><td id="Tab0" class="tab_on">生成采集地址</td><td class="tab_nav">&nbsp;</td></tr></tbody></table></td><td width="110"><div><img src="<?php echo base_url("skin/images/spacer.gif")?>" width="40" height="24" title="刷新" onclick="window.location.reload();" style="cursor:pointer;" alt=""><img src="<?php echo base_url("skin/images/spacer.gif")?>" width="20" height="24" title="后退" onclick="history.back(-1);" style="cursor:pointer;" alt=""><img src="<?php echo base_url("skin/images/spacer.g